<?php    class userProfile{        public static function userAuto($user_login, $user_password) {            if($user_login == $_POST['login'] && $user_password == $_POST['password']){                $usr = userByLogin::userAuto(mb_strtolower($user_login));                if($usr == null){                    echo "User does not autorised <br>";                    return null;                }                $usr_obj = new userView($usr);                return $usr_obj;            }else{                return null;            }        }    }