<?php    class userView{        public $login;        public $password;        public $info;        public $role;        public function __construct($user) {            $this->login = $user->{'login'};            $this->password = $user->{'password'};            $this->info = $user->{'info'};            $this->role = $user->{'role'};        }        public function getUsersInfo(){            echo 'Выполнен вход от имени пользователя: '.$this->info.'<br>';            echo 'Login: '.$this->login.'<br>';            echo 'Роль: '.$this->role.'<br>';        }        public static function getUsersArray(){            $users_list = array();            foreach (glob("../users/*.txt") as $filename) {                $file = file_get_contents($filename, FILE_USE_INCLUDE_PATH);                if($file == True){                    $json_decoding_file = json_decode($file);                    $users_list[$json_decoding_file->{'login'}] = $json_decoding_file;                }            }            return $users_list;        }        public function __toString()        {            return $this->info;        }    }